sys_script_include.script:
var FinErpUtils = Class.create();
FinErpUtils.prototype = {
    initialize: function() {},

    getGlideRecords: function(tableName, queryParams, orderByParams) {
        var glideRec = new GlideRecord(tableName);
        if (!(gs.nil(queryParams))) {
            Object.keys(queryParams).forEach(function(key) {
                glideRec.addQuery(key.toString(), queryParams[key].toString());
            });
        }
        if (!(gs.nil(orderByParams))) {
            orderByParams.forEach(function(param) {
                glideRec.orderBy(param.toString());
            });
        }
        glideRec.query();
        return glideRec;
    },

    checkIfIntegrationErrorExistsForPO: function(po_sys_id) {
        var integrationErrorRecs = this.getGlideRecords('sn_shop_erp_error_task', {
            purchase_order: po_sys_id.toString(),
            subtype: 'purchase_order',
            state: 1
        });
        return integrationErrorRecs.hasNext();
    },
    checkIfIntegrationErrorExistsForGR: function(gr_sys_id) {
        var integrationErrorRecs = this.getGlideRecords('sn_shop_erp_error_task', {
            purchase_order: gr_sys_id.toString(),
            subtype: 'receipt',
            state: 1
        });
        return integrationErrorRecs.hasNext();
    },
    checkIfIntegrationErrorExistsForIR: function(ir_sys_id) {
        var integrationErrorRecs = this.getGlideRecords('sn_shop_erp_error_task', {
            purchase_order: gr_sys_id.toString(),
            subtype: 'invoice',
            state: 1
        });
        return integrationErrorRecs.hasNext();
    },
    checkIfPluginDependeciesInstalled: function(scopeNames) {
        scopeNames.forEach(function(key) {
            var isInstalled = new sn_fcms_intg.FinErpUtils().getGlideRecords('sys_store_app', {
                scope: key
            }).hasNext();
            if (!isInstalled) {
                return false;
            }
        });
        return true;
    },
    checkResubmitCondition: function(current, entity) {
            if (entity == 'GR')
                return (this.checkIfPluginDependeciesInstalled(['sn_psm_sap_int']) && current.erp_gr_number == '' && current.status == 'pending_submission' && this.checkIfIntegrationErrorExistsForGR(current.purchase_order_line.purchase_order.sys_id));

            if (entity == "PO")
                return (this.checkIfPluginDependeciesInstalled(['sn_psm_sap_int']) && current.status == 'pending_submission' && current.erp_number == '' && this.checkIfIntegrationErrorExistsForPO(current.sys_id));

            if (entity == 'IR')
                return (this.checkIfPluginDependeciesInstalled(['sn_apm_sap_int']) && current.erp_number == '' && current.state == 'approved' && this.checkIfIntegrationErrorExistsForIR(current.purchase_order.sys_id));
    },
    type: 'FinErpUtils'
};
